let vendor = require('autoprefixer/lib/vendor')
let Declaration = require('autoprefixer/lib/declaration')
let Resolution = require('autoprefixer/lib/resolution')
let Transition = require('autoprefixer/lib/transition')
let Processor = require('autoprefixer/lib/processor')
let Supports = require('autoprefixer/lib/supports')
let Browsers = require('autoprefixer/lib/browsers')
let Selector = require('autoprefixer/lib/selector')
let AtRule = require('autoprefixer/lib/at-rule')
let Value = require('autoprefixer/lib/value')
let utils = require('autoprefixer/lib/utils')
Selector.hack(require('autoprefixer/lib/hacks/fullscreen'))
Selector.hack(require('autoprefixer/lib/hacks/placeholder'))
Selector.hack(require('autoprefixer/lib/hacks/placeholder-shown'))
Declaration.hack(require('autoprefixer/lib/hacks/flex'))
Declaration.hack(require('autoprefixer/lib/hacks/order'))
Declaration.hack(require('autoprefixer/lib/hacks/filter'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-end'))
Declaration.hack(require('autoprefixer/lib/hacks/animation'))
Declaration.hack(require('autoprefixer/lib/hacks/flex-flow'))
Declaration.hack(require('autoprefixer/lib/hacks/flex-grow'))
Declaration.hack(require('autoprefixer/lib/hacks/flex-wrap'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-area'))
Declaration.hack(require('autoprefixer/lib/hacks/place-self'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-start'))
Declaration.hack(require('autoprefixer/lib/hacks/align-self'))
Declaration.hack(require('autoprefixer/lib/hacks/appearance'))
Declaration.hack(require('autoprefixer/lib/hacks/flex-basis'))
Declaration.hack(require('autoprefixer/lib/hacks/mask-border'))
Declaration.hack(require('autoprefixer/lib/hacks/mask-composite'))
Declaration.hack(require('autoprefixer/lib/hacks/align-items'))
Declaration.hack(require('autoprefixer/lib/hacks/user-select'))
Declaration.hack(require('autoprefixer/lib/hacks/flex-shrink'))
Declaration.hack(require('autoprefixer/lib/hacks/break-props'))
Declaration.hack(require('autoprefixer/lib/hacks/color-adjust'))
Declaration.hack(require('autoprefixer/lib/hacks/writing-mode'))
Declaration.hack(require('autoprefixer/lib/hacks/border-image'))
Declaration.hack(require('autoprefixer/lib/hacks/align-content'))
Declaration.hack(require('autoprefixer/lib/hacks/border-radius'))
Declaration.hack(require('autoprefixer/lib/hacks/block-logical'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-template'))
Declaration.hack(require('autoprefixer/lib/hacks/inline-logical'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-row-align'))
Declaration.hack(require('autoprefixer/lib/hacks/transform-decl'))
Declaration.hack(require('autoprefixer/lib/hacks/flex-direction'))
Declaration.hack(require('autoprefixer/lib/hacks/image-rendering'))
Declaration.hack(require('autoprefixer/lib/hacks/backdrop-filter'))
Declaration.hack(require('autoprefixer/lib/hacks/background-clip'))
Declaration.hack(require('autoprefixer/lib/hacks/text-decoration'))
Declaration.hack(require('autoprefixer/lib/hacks/justify-content'))
Declaration.hack(require('autoprefixer/lib/hacks/background-size'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-row-column'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-rows-columns'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-column-align'))
Declaration.hack(require('autoprefixer/lib/hacks/overscroll-behavior'))
Declaration.hack(require('autoprefixer/lib/hacks/grid-template-areas'))
Declaration.hack(require('autoprefixer/lib/hacks/text-emphasis-position'))
Declaration.hack(require('autoprefixer/lib/hacks/text-decoration-skip-ink'))
Value.hack(require('autoprefixer/lib/hacks/gradient'))
Value.hack(require('autoprefixer/lib/hacks/intrinsic'))
Value.hack(require('autoprefixer/lib/hacks/pixelated'))
Value.hack(require('autoprefixer/lib/hacks/image-set'))
Value.hack(require('autoprefixer/lib/hacks/cross-fade'))
Value.hack(require('autoprefixer/lib/hacks/display-flex'))
Value.hack(require('autoprefixer/lib/hacks/display-grid'))
Value.hack(require('autoprefixer/lib/hacks/filter-value'))

let declsCache = new Map()

class Prefixes {
  constructor (data, browsers, options = {}) {
    this.data = data
    this.browsers = browsers
    this.options = options
    ;[this.add, this.remove] = this.preprocess(this.select(this.data))
    this.transition = new Transition(this)
    this.processor = new Processor(this)
  }

  /**
   * Return clone instance to remove all prefixes
   */
  cleaner () {
    if (this.cleanerCache) {
      return this.cleanerCache
    }

    if (this.browsers.selected.length) {
      let empty = new Browsers(this.browsers.data, [])
      this.cleanerCache = new Prefixes(this.data, empty, this.options)
    } else {
      return this
    }

    return this.cleanerCache
  }

  /**
   * Select prefixes from data, which is necessary for selected browsers
   */
  select (list) {
    let selected = { add: {}, remove: {} }

    for (let name in list) {
      let data = list[name]
      let add = data.browsers.map(i => {
        let params = i.split(' ')
        return {
          browser: `${params[0]} ${params[1]}`,
          note: params[2]
        }
      })

      let notes = add
        .filter(i => i.note)
        .map(i => `${this.browsers.prefix(i.browser)} ${i.note}`)
      notes = utils.uniq(notes)

      add = add
        .filter(i => this.browsers.isSelected(i.browser))
        .map(i => {
          let prefix = this.browsers.prefix(i.browser)
          if (i.note) {
            return `${prefix} ${i.note}`
          } else {
            return prefix
          }
        })
      add = this.sort(utils.uniq(add))

      if (this.options.flexbox === 'no-2009') {
        add = add.filter(i => !i.includes('2009'))
      }

      let all = data.browsers.map(i => this.browsers.prefix(i))
      if (data.mistakes) {
        all = all.concat(data.mistakes)
      }
      all = all.concat(notes)
      all = utils.uniq(all)

      if (add.length) {
        selected.add[name] = add
        if (add.length < all.length) {
          selected.remove[name] = all.filter(i => !add.includes(i))
        }
      } else {
        selected.remove[name] = all
      }
    }

    return selected
  }

  /**
   * Sort vendor prefixes
   */
  sort (prefixes) {
    return prefixes.sort((a, b) => {
      let aLength = utils.removeNote(a).length
      let bLength = utils.removeNote(b).length

      if (aLength === bLength) {
        return b.length - a.length
      } else {
        return bLength - aLength
      }
    })
  }

  /**
   * Cache prefixes data to fast CSS processing
   */
  preprocess (selected) {
    let add = {
      'selectors': [],
      '@supports': new Supports(Prefixes, this)
    }
    for (let name in selected.add) {
      let prefixes = selected.add[name]
      if (name === '@keyframes' || name === '@viewport') {
        add[name] = new AtRule(name, prefixes, this)
      } else if (name === '@resolution') {
        add[name] = new Resolution(name, prefixes, this)
      } else if (this.data[name].selector) {
        add.selectors.push(Selector.load(name, prefixes, this))
      } else {
        let props = this.data[name].props

        if (props) {
          let value = Value.load(name, prefixes, this)
          for (let prop of props) {
            if (!add[prop]) {
              add[prop] = { values: [] }
            }
            add[prop].values.push(value)
          }
        } else {
          let values = (add[name] && add[name].values) || []
          add[name] = Declaration.load(name, prefixes, this)
          add[name].values = values
        }
      }
    }

    let remove = { selectors: [] }
    for (let name in selected.remove) {
      let prefixes = selected.remove[name]
      if (this.data[name].selector) {
        let selector = Selector.load(name, prefixes)
        for (let prefix of prefixes) {
          remove.selectors.push(selector.old(prefix))
        }
      } else if (name === '@keyframes' || name === '@viewport') {
        for (let prefix of prefixes) {
          let prefixed = `@${prefix}${name.slice(1)}`
          remove[prefixed] = { remove: true }
        }
      } else if (name === '@resolution') {
        remove[name] = new Resolution(name, prefixes, this)
      } else {
        let props = this.data[name].props
        if (props) {
          let value = Value.load(name, [], this)
          for (let prefix of prefixes) {
            let old = value.old(prefix)
            if (old) {
              for (let prop of props) {
                if (!remove[prop]) {
                  remove[prop] = {}
                }
                if (!remove[prop].values) {
                  remove[prop].values = []
                }
                remove[prop].values.push(old)
              }
            }
          }
        } else {
          for (let p of prefixes) {
            let olds = this.decl(name).old(name, p)
            if (name === 'align-self') {
              let a = add[name] && add[name].prefixes
              if (a) {
                if (p === '-webkit- 2009' && a.includes('-webkit-')) {
                  continue
                } else if (p === '-webkit-' && a.includes('-webkit- 2009')) {
                  continue
                }
              }
            }
            for (let prefixed of olds) {
              if (!remove[prefixed]) {
                remove[prefixed] = {}
              }
              remove[prefixed].remove = true
            }
          }
        }
      }
    }

    return [add, remove]
  }

  /**
   * Declaration loader with caching
   */
  decl (prop) {
    if (!declsCache.has(prop)) {
      declsCache.set(prop, Declaration.load(prop))
    }

    return declsCache.get(prop)
  }

  /**
   * Return unprefixed version of property
   */
  unprefixed (prop) {
    let value = this.normalize(vendor.unprefixed(prop))
    if (value === 'flex-direction') {
      value = 'flex-flow'
    }
    return value
  }

  /**
   * Normalize prefix for remover
   */
  normalize (prop) {
    return this.decl(prop).normalize(prop)
  }

  /**
   * Return prefixed version of property
   */
  prefixed (prop, prefix) {
    prop = vendor.unprefixed(prop)
    return this.decl(prop).prefixed(prop, prefix)
  }

  /**
   * Return values, which must be prefixed in selected property
   */
  values (type, prop) {
    let data = this[type]

    let global = data['*'] && data['*'].values
    let values = data[prop] && data[prop].values

    if (global && values) {
      return utils.uniq(global.concat(values))
    } else {
      return global || values || []
    }
  }

  /**
   * Group declaration by unprefixed property to check them
   */
  group (decl) {
    let rule = decl.parent
    let index = rule.index(decl)
    let { length } = rule.nodes
    let unprefixed = this.unprefixed(decl.prop)

    let checker = (step, callback) => {
      index += step
      while (index >= 0 && index < length) {
        let other = rule.nodes[index]
        if (other.type === 'decl') {
          if (step === -1 && other.prop === unprefixed) {
            if (!Browsers.withPrefix(other.value)) {
              break
            }
          }

          if (this.unprefixed(other.prop) !== unprefixed) {
            break
          } else if (callback(other) === true) {
            return true
          }

          if (step === +1 && other.prop === unprefixed) {
            if (!Browsers.withPrefix(other.value)) {
              break
            }
          }
        }

        index += step
      }
      return false
    }

    return {
      up (callback) {
        return checker(-1, callback)
      },
      down (callback) {
        return checker(+1, callback)
      }
    }
  }
}

module.exports = Prefixes
